import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  Search, 
  MapPin, 
  Building, 
  Plus, 
  Trash2, 
  Play,
  Settings,
  Info,
  AlertCircle
} from 'lucide-react';
import toast from 'react-hot-toast';

const BUSINESS_TYPES = [
  'real estate agent',
  'restaurant',
  'dentist',
  'lawyer',
  'plumber',
  'electrician',
  'roofing',
  'HVAC',
  'auto repair',
  'hair salon',
  'fitness center',
  'veterinarian',
  'accountant',
  'insurance agent',
  'chiropractor',
  'massage therapist',
  'photographer',
  'florist',
  'bakery',
  'dry cleaner'
];

const REGIONS = [
  'Salt Lake City, UT',
  'Provo, UT',
  'Ogden, UT',
  'West Valley City, UT',
  'Orem, UT',
  'Sandy, UT',
  'West Jordan, UT',
  'Layton, UT',
  'Taylorsville, UT',
  'Murray, UT',
  'Bountiful, UT',
  'Draper, UT',
  'Riverton, UT',
  'Roy, UT',
  'Pleasant Grove, UT'
];

function LeadScraperForm({ onSubmit, isRunning, disabled }) {
  const [formData, setFormData] = useState({
    businessType: '',
    customBusinessType: '',
    regions: [''],
    customRegions: [''],
    useCustomBusiness: false,
    useCustomRegions: false,
    maxResults: 100,
    waitTime: 2
  });

  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};

    // Business type validation
    if (!formData.useCustomBusiness && !formData.businessType) {
      newErrors.businessType = 'Please select a business type';
    }
    if (formData.useCustomBusiness && !formData.customBusinessType.trim()) {
      newErrors.customBusinessType = 'Please enter a custom business type';
    }

    // Regions validation
    if (!formData.useCustomRegions) {
      const validRegions = formData.regions.filter(region => region.trim());
      if (validRegions.length === 0) {
        newErrors.regions = 'Please select at least one region';
      }
    } else {
      const validCustomRegions = formData.customRegions.filter(region => region.trim());
      if (validCustomRegions.length === 0) {
        newErrors.customRegions = 'Please enter at least one custom region';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const generateQueries = () => {
    const businessType = formData.useCustomBusiness 
      ? formData.customBusinessType.trim()
      : formData.businessType;

    const regions = formData.useCustomRegions
      ? formData.customRegions.filter(region => region.trim())
      : formData.regions.filter(region => region.trim());

    return regions.map(region => {
      const cleanRegion = region.replace(/\s+/g, '+').replace(/,/g, '');
      return `${cleanRegion}+${businessType.replace(/\s+/g, '+')}`;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      toast.error('Please fix the form errors before submitting');
      return;
    }

    const queries = generateQueries();
    
    if (queries.length === 0) {
      toast.error('No valid queries generated');
      return;
    }

    const submitData = {
      queries,
      businessType: formData.useCustomBusiness ? formData.customBusinessType : formData.businessType,
      regions: formData.useCustomRegions ? formData.customRegions : formData.regions,
      maxResults: formData.maxResults,
      waitTime: formData.waitTime
    };

    onSubmit(submitData);
    toast.success(`Starting scraper with ${queries.length} queries`);
  };

  const addRegion = () => {
    if (formData.useCustomRegions) {
      setFormData(prev => ({
        ...prev,
        customRegions: [...prev.customRegions, '']
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        regions: [...prev.regions, '']
      }));
    }
  };

  const removeRegion = (index) => {
    if (formData.useCustomRegions) {
      setFormData(prev => ({
        ...prev,
        customRegions: prev.customRegions.filter((_, i) => i !== index)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        regions: prev.regions.filter((_, i) => i !== index)
      }));
    }
  };

  const updateRegion = (index, value) => {
    if (formData.useCustomRegions) {
      setFormData(prev => ({
        ...prev,
        customRegions: prev.customRegions.map((region, i) => i === index ? value : region)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        regions: prev.regions.map((region, i) => i === index ? value : region)
      }));
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="card"
    >
      <div className="card-header">
        <h2 className="text-lg font-semibold flex items-center space-x-2">
          <Search className="h-5 w-5" />
          <span>Lead Scraper Configuration</span>
        </h2>
      </div>

      <form onSubmit={handleSubmit} className="card-body space-y-6">
        
        {/* Business Type Section */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            <Building className="inline h-4 w-4 mr-1" />
            Business Type
          </label>
          
          <div className="space-y-3">
            <div className="flex items-center space-x-2">
              <input
                type="radio"
                id="preset-business"
                name="businessTypeOption"
                checked={!formData.useCustomBusiness}
                onChange={() => setFormData(prev => ({ ...prev, useCustomBusiness: false }))}
                className="text-primary-600 focus:ring-primary-500"
              />
              <label htmlFor="preset-business" className="text-sm text-gray-700">
                Choose from preset options
              </label>
            </div>

            {!formData.useCustomBusiness && (
              <select
                value={formData.businessType}
                onChange={(e) => setFormData(prev => ({ ...prev, businessType: e.target.value }))}
                className="input"
                disabled={disabled}
              >
                <option value="">Select a business type...</option>
                {BUSINESS_TYPES.map(type => (
                  <option key={type} value={type}>
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </option>
                ))}
              </select>
            )}

            <div className="flex items-center space-x-2">
              <input
                type="radio"
                id="custom-business"
                name="businessTypeOption"
                checked={formData.useCustomBusiness}
                onChange={() => setFormData(prev => ({ ...prev, useCustomBusiness: true }))}
                className="text-primary-600 focus:ring-primary-500"
              />
              <label htmlFor="custom-business" className="text-sm text-gray-700">
                Enter custom business type
              </label>
            </div>

            {formData.useCustomBusiness && (
              <input
                type="text"
                value={formData.customBusinessType}
                onChange={(e) => setFormData(prev => ({ ...prev, customBusinessType: e.target.value }))}
                placeholder="e.g., yoga studio, coffee shop, auto mechanic"
                className="input"
                disabled={disabled}
              />
            )}
          </div>

          {errors.businessType && (
            <p className="text-sm text-danger-600 mt-1 flex items-center">
              <AlertCircle className="h-4 w-4 mr-1" />
              {errors.businessType}
            </p>
          )}
          {errors.customBusinessType && (
            <p className="text-sm text-danger-600 mt-1 flex items-center">
              <AlertCircle className="h-4 w-4 mr-1" />
              {errors.customBusinessType}
            </p>
          )}
        </div>

        {/* Regions Section */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            <MapPin className="inline h-4 w-4 mr-1" />
            Target Regions
          </label>

          <div className="space-y-3">
            <div className="flex items-center space-x-2">
              <input
                type="radio"
                id="preset-regions"
                name="regionsOption"
                checked={!formData.useCustomRegions}
                onChange={() => setFormData(prev => ({ ...prev, useCustomRegions: false }))}
                className="text-primary-600 focus:ring-primary-500"
              />
              <label htmlFor="preset-regions" className="text-sm text-gray-700">
                Choose from Utah cities
              </label>
            </div>

            {!formData.useCustomRegions && (
              <div className="space-y-2">
                {formData.regions.map((region, index) => (
                  <div key={index} className="flex items-center space-x-2">
                    <select
                      value={region}
                      onChange={(e) => updateRegion(index, e.target.value)}
                      className="input flex-1"
                      disabled={disabled}
                    >
                      <option value="">Select a region...</option>
                      {REGIONS.map(regionOption => (
                        <option key={regionOption} value={regionOption}>
                          {regionOption}
                        </option>
                      ))}
                    </select>
                    {formData.regions.length > 1 && (
                      <button
                        type="button"
                        onClick={() => removeRegion(index)}
                        className="p-2 text-danger-600 hover:bg-danger-50 rounded-lg"
                        disabled={disabled}
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}

            <div className="flex items-center space-x-2">
              <input
                type="radio"
                id="custom-regions"
                name="regionsOption"
                checked={formData.useCustomRegions}
                onChange={() => setFormData(prev => ({ ...prev, useCustomRegions: true }))}
                className="text-primary-600 focus:ring-primary-500"
              />
              <label htmlFor="custom-regions" className="text-sm text-gray-700">
                Enter custom regions
              </label>
            </div>

            {formData.useCustomRegions && (
              <div className="space-y-2">
                {formData.customRegions.map((region, index) => (
                  <div key={index} className="flex items-center space-x-2">
                    <input
                      type="text"
                      value={region}
                      onChange={(e) => updateRegion(index, e.target.value)}
                      placeholder="e.g., Denver, CO or New York, NY"
                      className="input flex-1"
                      disabled={disabled}
                    />
                    {formData.customRegions.length > 1 && (
                      <button
                        type="button"
                        onClick={() => removeRegion(index)}
                        className="p-2 text-danger-600 hover:bg-danger-50 rounded-lg"
                        disabled={disabled}
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}

            <button
              type="button"
              onClick={addRegion}
              className="btn btn-secondary flex items-center space-x-2"
              disabled={disabled}
            >
              <Plus className="h-4 w-4" />
              <span>Add Region</span>
            </button>
          </div>

          {errors.regions && (
            <p className="text-sm text-danger-600 mt-1 flex items-center">
              <AlertCircle className="h-4 w-4 mr-1" />
              {errors.regions}
            </p>
          )}
          {errors.customRegions && (
            <p className="text-sm text-danger-600 mt-1 flex items-center">
              <AlertCircle className="h-4 w-4 mr-1" />
              {errors.customRegions}
            </p>
          )}
        </div>

        {/* Advanced Settings */}
        <div className="border-t pt-4">
          <h3 className="text-sm font-medium text-gray-700 mb-3 flex items-center">
            <Settings className="h-4 w-4 mr-1" />
            Advanced Settings
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Max Results per Query
              </label>
              <input
                type="number"
                value={formData.maxResults}
                onChange={(e) => setFormData(prev => ({ ...prev, maxResults: parseInt(e.target.value) }))}
                min="10"
                max="1000"
                className="input"
                disabled={disabled}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Wait Time (seconds)
              </label>
              <input
                type="number"
                value={formData.waitTime}
                onChange={(e) => setFormData(prev => ({ ...prev, waitTime: parseInt(e.target.value) }))}
                min="1"
                max="10"
                className="input"
                disabled={disabled}
              />
            </div>
          </div>
        </div>

        {/* Info Box */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-start space-x-2">
            <Info className="h-5 w-5 text-blue-600 mt-0.5" />
            <div className="text-sm text-blue-800">
              <p className="font-medium mb-1">How it works:</p>
              <ul className="list-disc list-inside space-y-1 text-blue-700">
                <li>Searches Google Maps for businesses in your specified regions</li>
                <li>Extracts business websites from search results</li>
                <li>Scrapes email addresses from each website</li>
                <li>Filters and deduplicates results automatically</li>
              </ul>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={disabled || isRunning}
          className={`w-full btn ${isRunning ? 'btn-disabled' : 'btn-primary'} flex items-center justify-center space-x-2 py-3`}
        >
          <Play className="h-5 w-5" />
          <span>{isRunning ? 'Scraping in Progress...' : 'Start Lead Scraping'}</span>
        </button>
      </form>
    </motion.div>
  );
}

export default LeadScraperForm;
