import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Toaster } from 'react-hot-toast';
import { 
  Search, 
  MapPin, 
  Mail, 
  Play, 
  Pause, 
  Download, 
  Settings,
  BarChart3,
  Users,
  Globe,
  Zap,
  CheckCircle,
  AlertCircle,
  Clock,
  Loader2
} from 'lucide-react';

import LeadScraperForm from './components/LeadScraperForm';
import ProgressDashboard from './components/ProgressDashboard';
import ResultsDisplay from './components/ResultsDisplay';
import StatusIndicator from './components/StatusIndicator';
import { useWebSocket } from './hooks/useWebSocket';
import { scrapingService } from './services/scrapingService';

function App() {
  const [isRunning, setIsRunning] = useState(false);
  const [currentJob, setCurrentJob] = useState(null);
  const [results, setResults] = useState([]);
  const [stats, setStats] = useState({
    totalQueries: 0,
    processedQueries: 0,
    totalEmails: 0,
    uniqueEmails: 0,
    estimatedTime: 0,
    startTime: null
  });

  const { connectionStatus, lastMessage } = useWebSocket('ws://localhost:5000');

  // Handle WebSocket messages
  useEffect(() => {
    if (lastMessage) {
      const data = JSON.parse(lastMessage.data);
      
      switch (data.type) {
        case 'status_update':
          setStats(prev => ({ ...prev, ...data.stats }));
          break;
        case 'new_emails':
          setResults(prev => [...prev, ...data.emails]);
          break;
        case 'job_complete':
          setIsRunning(false);
          setCurrentJob(null);
          break;
        case 'error':
          console.error('Scraping error:', data.error);
          setIsRunning(false);
          break;
        default:
          break;
      }
    }
  }, [lastMessage]);

  const handleStartScraping = async (formData) => {
    try {
      setIsRunning(true);
      setResults([]);
      setStats({
        totalQueries: formData.queries.length,
        processedQueries: 0,
        totalEmails: 0,
        uniqueEmails: 0,
        estimatedTime: formData.queries.length * 30, // 30 seconds per query estimate
        startTime: new Date()
      });

      const job = await scrapingService.startScraping(formData);
      setCurrentJob(job);
    } catch (error) {
      console.error('Failed to start scraping:', error);
      setIsRunning(false);
    }
  };

  const handleStopScraping = async () => {
    if (currentJob) {
      try {
        await scrapingService.stopScraping(currentJob.id);
        setIsRunning(false);
        setCurrentJob(null);
      } catch (error) {
        console.error('Failed to stop scraping:', error);
      }
    }
  };

  const handleDownloadResults = () => {
    if (results.length > 0) {
      const csvContent = "data:text/csv;charset=utf-8," 
        + "Email,Source URL,Query\n"
        + results.map(result => `${result.email},${result.sourceUrl},${result.query}`).join("\n");
      
      const encodedUri = encodeURI(csvContent);
      const link = document.createElement("a");
      link.setAttribute("href", encodedUri);
      link.setAttribute("download", `leads_${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <Toaster position="top-right" />
      
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center space-x-3">
              <div className="bg-gradient-primary p-2 rounded-lg">
                <Search className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gradient">Lead Scraper Pro</h1>
                <p className="text-sm text-gray-600">Powered by n8n automation</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <StatusIndicator status={connectionStatus} />
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Globe className="h-4 w-4" />
                <span>Google Maps</span>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          
          {/* Left Column - Form */}
          <div className="lg:col-span-1">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
            >
              <LeadScraperForm
                onSubmit={handleStartScraping}
                isRunning={isRunning}
                disabled={isRunning}
              />
            </motion.div>
          </div>

          {/* Right Column - Progress & Results */}
          <div className="lg:col-span-2 space-y-6">
            
            {/* Control Panel */}
            <motion.div
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.1 }}
              className="card"
            >
              <div className="card-header">
                <div className="flex items-center justify-between">
                  <h2 className="text-lg font-semibold flex items-center space-x-2">
                    <BarChart3 className="h-5 w-5" />
                    <span>Control Panel</span>
                  </h2>
                  <div className="flex items-center space-x-2">
                    {isRunning ? (
                      <button
                        onClick={handleStopScraping}
                        className="btn btn-danger flex items-center space-x-2"
                      >
                        <Pause className="h-4 w-4" />
                        <span>Stop</span>
                      </button>
                    ) : (
                      <button
                        onClick={handleDownloadResults}
                        disabled={results.length === 0}
                        className="btn btn-secondary flex items-center space-x-2"
                      >
                        <Download className="h-4 w-4" />
                        <span>Download</span>
                      </button>
                    )}
                  </div>
                </div>
              </div>
              
              <div className="card-body">
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-primary-600">{stats.totalQueries}</div>
                    <div className="text-sm text-gray-600">Total Queries</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-success-600">{stats.processedQueries}</div>
                    <div className="text-sm text-gray-600">Processed</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-warning-600">{stats.totalEmails}</div>
                    <div className="text-sm text-gray-600">Emails Found</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-600">{stats.uniqueEmails}</div>
                    <div className="text-sm text-gray-600">Unique Emails</div>
                  </div>
                </div>
              </div>
            </motion.div>

            {/* Progress Dashboard */}
            <AnimatePresence>
              {isRunning && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <ProgressDashboard 
                    stats={stats}
                    isRunning={isRunning}
                  />
                </motion.div>
              )}
            </AnimatePresence>

            {/* Results Display */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <ResultsDisplay 
                results={results}
                isLoading={isRunning}
              />
            </motion.div>
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Zap className="h-5 w-5 text-primary-600" />
              <span className="text-sm text-gray-600">
                Powered by n8n automation workflow
              </span>
            </div>
            <div className="text-sm text-gray-500">
              © 2024 Lead Scraper Pro. Built with React & n8n.
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
