import React, { useState, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Mail, 
  Search, 
  Copy, 
  ExternalLink, 
  Filter,
  SortAsc,
  SortDesc,
  Eye,
  EyeOff,
  CheckCircle,
  Clock,
  Globe
} from 'lucide-react';
import toast from 'react-hot-toast';

const ResultsDisplay = ({ results, isLoading }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('timestamp');
  const [sortOrder, setSortOrder] = useState('desc');
  const [showDetails, setShowDetails] = useState(false);
  const [selectedEmails, setSelectedEmails] = useState(new Set());

  // Filter and sort results
  const filteredAndSortedResults = useMemo(() => {
    let filtered = results.filter(result => 
      result.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      result.query?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      result.sourceUrl?.toLowerCase().includes(searchTerm.toLowerCase())
    );

    filtered.sort((a, b) => {
      let aValue, bValue;
      
      switch (sortBy) {
        case 'email':
          aValue = a.email.toLowerCase();
          bValue = b.email.toLowerCase();
          break;
        case 'query':
          aValue = a.query?.toLowerCase() || '';
          bValue = b.query?.toLowerCase() || '';
          break;
        case 'domain':
          aValue = a.email.split('@')[1]?.toLowerCase() || '';
          bValue = b.email.split('@')[1]?.toLowerCase() || '';
          break;
        case 'timestamp':
        default:
          aValue = new Date(a.timestamp || 0);
          bValue = new Date(b.timestamp || 0);
          break;
      }

      if (sortOrder === 'asc') {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });

    return filtered;
  }, [results, searchTerm, sortBy, sortOrder]);

  // Get unique domains for stats
  const uniqueDomains = useMemo(() => {
    const domains = new Set();
    results.forEach(result => {
      const domain = result.email.split('@')[1];
      if (domain) domains.add(domain);
    });
    return domains.size;
  }, [results]);

  const handleCopyEmail = (email) => {
    navigator.clipboard.writeText(email);
    toast.success('Email copied to clipboard!');
  };

  const handleCopyAll = () => {
    const emails = filteredAndSortedResults.map(result => result.email).join('\n');
    navigator.clipboard.writeText(emails);
    toast.success(`${filteredAndSortedResults.length} emails copied to clipboard!`);
  };

  const handleSelectEmail = (email) => {
    const newSelected = new Set(selectedEmails);
    if (newSelected.has(email)) {
      newSelected.delete(email);
    } else {
      newSelected.add(email);
    }
    setSelectedEmails(newSelected);
  };

  const handleSelectAll = () => {
    if (selectedEmails.size === filteredAndSortedResults.length) {
      setSelectedEmails(new Set());
    } else {
      setSelectedEmails(new Set(filteredAndSortedResults.map(result => result.email)));
    }
  };

  const handleCopySelected = () => {
    const selectedEmailsList = Array.from(selectedEmails).join('\n');
    navigator.clipboard.writeText(selectedEmailsList);
    toast.success(`${selectedEmails.size} selected emails copied to clipboard!`);
  };

  const formatTimestamp = (timestamp) => {
    if (!timestamp) return 'Unknown';
    return new Date(timestamp).toLocaleTimeString();
  };

  const formatQuery = (query) => {
    if (!query) return 'Unknown';
    return query.replace(/\+/g, ' ').toLowerCase()
      .split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const getDomain = (email) => {
    return email.split('@')[1] || 'unknown';
  };

  return (
    <div className="card">
      <div className="card-header">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold flex items-center space-x-2">
            <Mail className="h-5 w-5" />
            <span>Scraped Emails</span>
            <span className="bg-primary-100 text-primary-800 px-2 py-1 rounded-full text-sm">
              {filteredAndSortedResults.length}
            </span>
          </h3>
          
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowDetails(!showDetails)}
              className="btn btn-secondary flex items-center space-x-1"
            >
              {showDetails ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
              <span>{showDetails ? 'Hide' : 'Show'} Details</span>
            </button>
            
            {filteredAndSortedResults.length > 0 && (
              <button
                onClick={handleCopyAll}
                className="btn btn-primary flex items-center space-x-1"
              >
                <Copy className="h-4 w-4" />
                <span>Copy All</span>
              </button>
            )}
          </div>
        </div>
      </div>

      <div className="card-body">
        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="text-center p-3 bg-blue-50 rounded-lg">
            <div className="text-2xl font-bold text-blue-900">{results.length}</div>
            <div className="text-sm text-blue-600">Total Emails</div>
          </div>
          <div className="text-center p-3 bg-green-50 rounded-lg">
            <div className="text-2xl font-bold text-green-900">{uniqueDomains}</div>
            <div className="text-sm text-green-600">Unique Domains</div>
          </div>
          <div className="text-center p-3 bg-purple-50 rounded-lg">
            <div className="text-2xl font-bold text-purple-900">{selectedEmails.size}</div>
            <div className="text-sm text-purple-600">Selected</div>
          </div>
        </div>

        {/* Search and Filter Controls */}
        <div className="flex flex-col md:flex-row gap-4 mb-6">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search emails, queries, or domains..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input pl-10"
            />
          </div>
          
          <div className="flex items-center space-x-2">
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="input"
            >
              <option value="timestamp">Sort by Time</option>
              <option value="email">Sort by Email</option>
              <option value="domain">Sort by Domain</option>
              <option value="query">Sort by Query</option>
            </select>
            
            <button
              onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
              className="btn btn-secondary"
            >
              {sortOrder === 'asc' ? <SortAsc className="h-4 w-4" /> : <SortDesc className="h-4 w-4" />}
            </button>
          </div>
        </div>

        {/* Bulk Actions */}
        {filteredAndSortedResults.length > 0 && (
          <div className="flex items-center justify-between mb-4 p-3 bg-gray-50 rounded-lg">
            <div className="flex items-center space-x-2">
              <input
                type="checkbox"
                checked={selectedEmails.size === filteredAndSortedResults.length}
                onChange={handleSelectAll}
                className="rounded border-gray-300"
              />
              <span className="text-sm text-gray-700">
                Select All ({filteredAndSortedResults.length})
              </span>
            </div>
            
            {selectedEmails.size > 0 && (
              <button
                onClick={handleCopySelected}
                className="btn btn-primary btn-sm flex items-center space-x-1"
              >
                <Copy className="h-3 w-3" />
                <span>Copy Selected ({selectedEmails.size})</span>
              </button>
            )}
          </div>
        )}

        {/* Results List */}
        <div className="space-y-2 max-h-96 overflow-y-auto">
          <AnimatePresence>
            {isLoading && filteredAndSortedResults.length === 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="text-center py-8"
              >
                <div className="inline-flex items-center space-x-2 text-gray-500">
                  <Clock className="h-5 w-5 animate-spin" />
                  <span>Waiting for results...</span>
                </div>
              </motion.div>
            )}

            {!isLoading && filteredAndSortedResults.length === 0 && results.length === 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="text-center py-8"
              >
                <Mail className="h-12 w-12 text-gray-300 mx-auto mb-2" />
                <p className="text-gray-500">No emails found yet</p>
                <p className="text-sm text-gray-400">Start scraping to see results here</p>
              </motion.div>
            )}

            {!isLoading && filteredAndSortedResults.length === 0 && results.length > 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="text-center py-8"
              >
                <Search className="h-12 w-12 text-gray-300 mx-auto mb-2" />
                <p className="text-gray-500">No emails match your search</p>
                <p className="text-sm text-gray-400">Try adjusting your search terms</p>
              </motion.div>
            )}

            {filteredAndSortedResults.map((result, index) => (
              <motion.div
                key={`${result.email}-${index}`}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3, delay: index * 0.05 }}
                className={`border rounded-lg p-3 hover:bg-gray-50 transition-colors ${
                  selectedEmails.has(result.email) ? 'bg-blue-50 border-blue-200' : 'bg-white'
                }`}
              >
                <div className="flex items-center space-x-3">
                  <input
                    type="checkbox"
                    checked={selectedEmails.has(result.email)}
                    onChange={() => handleSelectEmail(result.email)}
                    className="rounded border-gray-300"
                  />
                  
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2">
                      <span className="font-medium text-gray-900 truncate">
                        {result.email}
                      </span>
                      <span className="badge badge-primary">
                        {getDomain(result.email)}
                      </span>
                    </div>
                    
                    {showDetails && (
                      <div className="mt-2 space-y-1 text-sm text-gray-600">
                        <div className="flex items-center space-x-2">
                          <Search className="h-3 w-3" />
                          <span>Query: {formatQuery(result.query)}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Globe className="h-3 w-3" />
                          <span>Source: {result.sourceUrl}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Clock className="h-3 w-3" />
                          <span>Found: {formatTimestamp(result.timestamp)}</span>
                        </div>
                      </div>
                    )}
                  </div>
                  
                  <div className="flex items-center space-x-1">
                    <button
                      onClick={() => handleCopyEmail(result.email)}
                      className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
                      title="Copy email"
                    >
                      <Copy className="h-4 w-4" />
                    </button>
                    
                    {result.sourceUrl && (
                      <a
                        href={result.sourceUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
                        title="Visit source"
                      >
                        <ExternalLink className="h-4 w-4" />
                      </a>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

export default ResultsDisplay;
