import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  Search, 
  MapPin, 
  Building, 
  Plus, 
  Trash2, 
  Play,
  Settings,
  Info,
  AlertCircle,
  Acre
} from 'lucide-react';
import toast from 'react-hot-toast';

const PROPERTY_TYPES = [
  { value: 'D1', label: 'Qualified Agricultural/Timber Land' },
  { value: 'E1', label: 'Non-Qualified Agricultural Land' },
  { value: 'C3', label: 'Vacant Rural Land' },
  { value: 'A1', label: 'Residential/Single Family' },
  { value: 'F1', label: 'Commercial Real Estate' },
  { value: 'F2', label: 'Industrial Real Estate' }
];

const TEXAS_COUNTIES = [
  'Anderson County',
  'Henderson County', 
  'Cherokee County',
  'Smith County',
  'Rusk County',
  'Freestone County',
  'Navarro County',
  'Van Zandt County'
];

function PropertyScraperForm({ onSubmit, isRunning, disabled }) {
  const [formData, setFormData] = useState({
    minAcres: 50,
    maxAcres: 10000,
    propertyTypes: ['D1', 'E1'], // Default to agricultural land
    counties: ['Anderson County'],
    useCustomCounties: false,
    customCounties: [''],
    waitTime: 2
  });

  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};

    // Acreage validation
    if (!formData.minAcres || formData.minAcres < 1) {
      newErrors.minAcres = 'Minimum acreage must be at least 1';
    }
    if (!formData.maxAcres || formData.maxAcres < formData.minAcres) {
      newErrors.maxAcres = 'Maximum acreage must be greater than minimum';
    }

    // Property types validation
    if (!formData.propertyTypes || formData.propertyTypes.length === 0) {
      newErrors.propertyTypes = 'Please select at least one property type';
    }

    // Counties validation
    if (!formData.useCustomCounties) {
      if (!formData.counties || formData.counties.length === 0) {
        newErrors.counties = 'Please select at least one county';
      }
    } else {
      const validCustomCounties = formData.customCounties.filter(county => county.trim());
      if (validCustomCounties.length === 0) {
        newErrors.customCounties = 'Please enter at least one custom county';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      toast.error('Please fix the form errors before submitting');
      return;
    }

    const counties = formData.useCustomCounties
      ? formData.customCounties.filter(county => county.trim())
      : formData.counties;

    const submitData = {
      minAcres: formData.minAcres,
      maxAcres: formData.maxAcres,
      propertyTypes: formData.propertyTypes,
      counties: counties,
      waitTime: formData.waitTime
    };

    onSubmit(submitData);
    toast.success(`Starting property scraper for ${counties.length} counties`);
  };

  const addCounty = () => {
    if (formData.useCustomCounties) {
      setFormData(prev => ({
        ...prev,
        customCounties: [...prev.customCounties, '']
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        counties: [...prev.counties, '']
      }));
    }
  };

  const removeCounty = (index) => {
    if (formData.useCustomCounties) {
      setFormData(prev => ({
        ...prev,
        customCounties: prev.customCounties.filter((_, i) => i !== index)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        counties: prev.counties.filter((_, i) => i !== index)
      }));
    }
  };

  const updateCounty = (index, value) => {
    if (formData.useCustomCounties) {
      setFormData(prev => ({
        ...prev,
        customCounties: prev.customCounties.map((county, i) => i === index ? value : county)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        counties: prev.counties.map((county, i) => i === index ? value : county)
      }));
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
      <div className="bg-gradient-to-r from-green-600 to-emerald-600 px-8 py-6">
        <div className="flex items-center space-x-3">
          <div className="p-2 bg-white/20 rounded-lg">
            <Search className="w-6 h-6 text-white" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-white">Anderson County Property Scraper</h2>
            <p className="text-green-100 mt-1">Find large acreage properties in Texas</p>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="p-8 space-y-8">
        {/* Acreage Range Section */}
        <div className="space-y-6">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-green-100 rounded-lg">
              <MapPin className="w-5 h-5 text-green-600" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900">Acreage Range</h3>
          </div>

          <div className="grid md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Minimum Acres
              </label>
              <input
                type="number"
                min="1"
                value={formData.minAcres}
                onChange={(e) => setFormData(prev => ({ ...prev, minAcres: parseInt(e.target.value) || 1 }))}
                className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all ${
                  errors.minAcres ? 'border-red-300 bg-red-50' : 'border-gray-300'
                }`}
                placeholder="50"
              />
              {errors.minAcres && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.minAcres}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Maximum Acres
              </label>
              <input
                type="number"
                min="1"
                value={formData.maxAcres}
                onChange={(e) => setFormData(prev => ({ ...prev, maxAcres: parseInt(e.target.value) || 10000 }))}
                className={`w-full px-4 py-3 border rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all ${
                  errors.maxAcres ? 'border-red-300 bg-red-50' : 'border-gray-300'
                }`}
                placeholder="10000"
              />
              {errors.maxAcres && (
                <p className="mt-1 text-sm text-red-600 flex items-center">
                  <AlertCircle className="w-4 h-4 mr-1" />
                  {errors.maxAcres}
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Property Types Section */}
        <div className="space-y-6">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <Building className="w-5 h-5 text-blue-600" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900">Property Types</h3>
          </div>

          <div className="grid md:grid-cols-2 gap-3">
            {PROPERTY_TYPES.map((type) => (
              <label key={type.value} className="flex items-center p-4 border rounded-xl cursor-pointer hover:bg-gray-50 transition-colors">
                <input
                  type="checkbox"
                  checked={formData.propertyTypes.includes(type.value)}
                  onChange={(e) => {
                    if (e.target.checked) {
                      setFormData(prev => ({
                        ...prev,
                        propertyTypes: [...prev.propertyTypes, type.value]
                      }));
                    } else {
                      setFormData(prev => ({
                        ...prev,
                        propertyTypes: prev.propertyTypes.filter(t => t !== type.value)
                      }));
                    }
                  }}
                  className="h-4 w-4 text-green-600 focus:ring-green-500 border-gray-300 rounded"
                />
                <div className="ml-3">
                  <span className="text-sm font-medium text-gray-900">{type.label}</span>
                  <span className="text-xs text-gray-500 block">({type.value})</span>
                </div>
              </label>
            ))}
          </div>
          {errors.propertyTypes && (
            <p className="text-sm text-red-600 flex items-center">
              <AlertCircle className="w-4 h-4 mr-1" />
              {errors.propertyTypes}
            </p>
          )}
        </div>

        {/* Counties Section */}
        <div className="space-y-6">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-purple-100 rounded-lg">
              <MapPin className="w-5 h-5 text-purple-600" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900">Target Counties</h3>
          </div>

          <div className="flex items-center space-x-4">
            <label className="flex items-center">
              <input
                type="radio"
                checked={!formData.useCustomCounties}
                onChange={() => setFormData(prev => ({ ...prev, useCustomCounties: false }))}
                className="h-4 w-4 text-green-600 focus:ring-green-500"
              />
              <span className="ml-2 text-sm font-medium text-gray-700">Preset Counties</span>
            </label>
            <label className="flex items-center">
              <input
                type="radio"
                checked={formData.useCustomCounties}
                onChange={() => setFormData(prev => ({ ...prev, useCustomCounties: true }))}
                className="h-4 w-4 text-green-600 focus:ring-green-500"
              />
              <span className="ml-2 text-sm font-medium text-gray-700">Custom Counties</span>
            </label>
          </div>

          {!formData.useCustomCounties ? (
            <div className="space-y-3">
              {formData.counties.map((county, index) => (
                <div key={index} className="flex items-center space-x-3">
                  <select
                    value={county}
                    onChange={(e) => updateCounty(index, e.target.value)}
                    className="flex-1 px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  >
                    <option value="">Select a county...</option>
                    {TEXAS_COUNTIES.map((countyOption) => (
                      <option key={countyOption} value={countyOption}>
                        {countyOption}
                      </option>
                    ))}
                  </select>
                  {formData.counties.length > 1 && (
                    <button
                      type="button"
                      onClick={() => removeCounty(index)}
                      className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  )}
                </div>
              ))}
              <button
                type="button"
                onClick={addCounty}
                className="flex items-center space-x-2 px-4 py-2 text-green-600 bg-green-50 rounded-lg hover:bg-green-100 transition-colors"
              >
                <Plus className="w-4 h-4" />
                <span>Add County</span>
              </button>
            </div>
          ) : (
            <div className="space-y-3">
              {formData.customCounties.map((county, index) => (
                <div key={index} className="flex items-center space-x-3">
                  <input
                    type="text"
                    value={county}
                    onChange={(e) => updateCounty(index, e.target.value)}
                    placeholder="Enter county name (e.g., Harris County, TX)"
                    className="flex-1 px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  />
                  {formData.customCounties.length > 1 && (
                    <button
                      type="button"
                      onClick={() => removeCounty(index)}
                      className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  )}
                </div>
              ))}
              <button
                type="button"
                onClick={addCounty}
                className="flex items-center space-x-2 px-4 py-2 text-green-600 bg-green-50 rounded-lg hover:bg-green-100 transition-colors"
              >
                <Plus className="w-4 h-4" />
                <span>Add County</span>
              </button>
            </div>
          )}
          {errors.counties && (
            <p className="text-sm text-red-600 flex items-center">
              <AlertCircle className="w-4 h-4 mr-1" />
              {errors.counties}
            </p>
          )}
          {errors.customCounties && (
            <p className="text-sm text-red-600 flex items-center">
              <AlertCircle className="w-4 h-4 mr-1" />
              {errors.customCounties}
            </p>
          )}
        </div>

        {/* Advanced Settings */}
        <div className="space-y-6">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-orange-100 rounded-lg">
              <Settings className="w-5 h-5 text-orange-600" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900">Advanced Settings</h3>
          </div>

          <div className="grid md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Wait Time (seconds)
              </label>
              <input
                type="number"
                min="1"
                max="10"
                value={formData.waitTime}
                onChange={(e) => setFormData(prev => ({ ...prev, waitTime: parseInt(e.target.value) || 2 }))}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-green-500 focus:border-transparent"
              />
              <p className="mt-1 text-xs text-gray-500">
                Delay between requests to be respectful to servers
              </p>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex items-center justify-between pt-6 border-t border-gray-200">
          <div className="flex items-center space-x-2 text-sm text-gray-600">
            <Info className="w-4 h-4" />
            <span>Searches Anderson County property records for large acreage properties</span>
          </div>

          <button
            type="submit"
            disabled={disabled || isRunning}
            className={`
              flex items-center space-x-3 px-8 py-4 rounded-xl font-semibold transition-all
              ${
                disabled || isRunning
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-gradient-to-r from-green-600 to-emerald-600 text-white hover:from-green-700 hover:to-emerald-700 shadow-lg hover:shadow-xl'
              }
            `}
          >
            <Play className="w-5 h-5" />
            <span>{isRunning ? 'Scraping Properties...' : 'Start Property Scraping'}</span>
          </button>
        </div>
      </form>
    </div>
  );
}

export default PropertyScraperForm;
